mapper.select:
select count(id)
from user
where id=#{userId};

select id, password, name, email, address, birth, point, job, married 
from user
where id=#{id}

select id, password, name, email, address, birth, point, job, married 
from user
where id=#{id}

select id, dongCode
from interest_area
where user_id=#{userId}

select dongName
from dongCode
where dongCode = #{dongCode};

select dongCode
from dongCode
where sidoName = #{sidoName}
and gugunName = #{gugunName}
and dongName = #{dongName};

mapper.delete:
delete from interest_area
where id = #{id}
and user_id = #{userId}
